(html5QrCode) => {
    fetch("{% url 'identify_child' %}", {
        method: 'POST',
        headers: {
            'Content-Type': 'application/x-www-form-urlencoded',
            'X-CSRFToken': '{{ csrf_token }}',
        },
        body: new URLSearchParams({ qr_data: decodedText }),
    })
    .then(response => response.json())
    .then(data => {
        if (data.success) {
            document.getElementById('scanResult').innerText = `Ребёнок: ${data.child_name}, Дата рождения: ${data.birth_date}`;
        } else {
            document.getElementById('scanResult').innerText = `Ошибка: ${data.error}`;
        }
    });
}
